import { Phone } from "lucide-react";

export function OrderManagement() {
  const { orders, updateOrder, isUpdating } = useOrders();

  const getStatusBadge = (status: string) => {
    const statusStyles = {
      pending: "status-pending bg-amber-100 text-amber-700",
      in_progress: "status-progress bg-blue-100 text-blue-700", 
      delivered: "status-delivered bg-green-100 text-green-700",
      cancelled: "bg-red-100 text-red-700",
    };

    return statusStyles[status as keyof typeof statusStyles] || statusStyles.pending;
  };

  const getNextStatus = (currentStatus: string) => {
    const statusFlow = {
      pending: { next: 'in_progress', label: 'Mark In Progress' },
      in_progress: { next: 'delivered', label: 'Mark Delivered' },
      delivered: { next: 'delivered', label: 'Completed' },
      cancelled: { next: 'cancelled', label: 'Cancelled' },
    };

    return statusFlow[currentStatus as keyof typeof statusFlow] || statusFlow.pending;
  };

  const handleStatusUpdate = (orderId: string, newStatus: string) => {
    updateOrder({ id: orderId, updates: { status: newStatus } });
  };

  if (!orders.length) {
    return (
      <Card className="p-6">
        <p className="text-center text-muted-foreground" data-testid="text-no-orders">
          No orders found
        </p>
      </Card>
    );
  }

  return (
    <Card className="p-6">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-foreground">Recent Orders</h3>
        <div className="flex gap-2">
          <Button size="sm" variant="default">All</Button>
          <Button size="sm" variant="outline">Pending</Button>
        </div>
      </div>

      <div className="space-y-4">
        {orders.map((orderData: any) => {
          const { order, user, service } = orderData;
          const nextStatus = getNextStatus(order.status);

          return (
            <div 
              key={order.id} 
              data-testid={`order-card-${order.id}`}
              className="border border-border rounded-lg p-4"
            >
              <div className="flex items-center justify-between mb-2">
                <div>
                  <p className="font-semibold text-foreground" data-testid={`order-id-${order.id}`}>
                    #{order.id.substring(0, 8)}
                  </p>
                  <p className="text-sm text-muted-foreground" data-testid={`customer-name-${order.id}`}>
                    {user.username}
                  </p>
                </div>
                <div className="text-right">
                  <Badge 
                    className={`order-status ${getStatusBadge(order.status)}`}
                    data-testid={`order-status-${order.id}`}
                  >
                    {order.status.replace('_', ' ').toUpperCase()}
                  </Badge>
                </div>
              </div>

              <div className="flex items-center justify-between mb-3">
                <p className="text-sm text-muted-foreground" data-testid={`order-details-${order.id}`}>
                  {service.name} â€¢ {order.quantity} {service.pricingUnit}s
                </p>
                <p className="font-semibold text-primary" data-testid={`order-total-${order.id}`}>
                  ${Number(order.total).toFixed(2)}
                </p>
              </div>

              <div className="flex gap-2">
                {order.status !== 'delivered' && order.status !== 'cancelled' && (
                  <Button
                    data-testid={`button-update-status-${order.id}`}
                    size="sm"
                    className="flex-1"
                    disabled={isUpdating}
                    onClick={() => handleStatusUpdate(order.id, nextStatus.next)}
                  >
                    {nextStatus.label}
                  </Button>
                )}
                <Button
                  data-testid={`button-call-customer-${order.id}`}
                  size="sm"
                  variant="outline"
                  className="px-4"
                >
                  <Phone size={16} />
                </Button>
              </div>
            </div>
          );
        })}
      </div>
    </Card>
  );
}

client/src/components/auth/auth-form.tsx.content:
import { useState } from "react";
import { signInWithEmailAndPassword, createUserWithEmailAndPassword } from "firebase/auth";
import { auth, signInWithGoogle } from "@/lib/firebase";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { Apple } from "lucide-react";

export function AuthForm() {
  const [isSignUp, setIsSignUp] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const { toast } = useToast();
  const [, navigate] = useLocation();

  const handleEmailAuth = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (isSignUp) {
        await createUserWithEmailAndPassword(auth, email, password);
      } else {
        await signInWithEmailAndPassword(auth, email, password);
      }
      navigate("/dashboard");
      toast({
        title: "Success!",
        description: `Successfully ${isSignUp ? 'signed up' : 'signed in'}`,
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleSignIn = async () => {
    try {
      await signInWithGoogle();
      // Redirect will be handled by auth state change
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  return (
    <div className="w-full max-w-sm space-y-4">
      <form onSubmit={handleEmailAuth} className="space-y-4 mb-8">
        <div>
          <Input
            data-testid="input-email"
            type="email"
            placeholder="Email address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full px-4 py-4 bg-white/10 border border-white/20 rounded-xl text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-white/30"
            required
          />
        </div>
        <div>
          <Input
            data-testid="input-password"
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-4 py-4 bg-white/10 border border-white/20 rounded-xl text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-white/30"
            required
          />
        </div>

        <Button
          data-testid="button-signin"
          type="submit"
          disabled={loading}
          className="w-full bg-white text-blue-600 py-4 rounded-xl font-semibold transition-all hover:bg-blue-50"
        >
          {loading ? "Processing..." : isSignUp ? "Sign Up" : "Sign In"}
        </Button>
      </form>

      <div className="flex items-center my-6">
        <div className="flex-1 h-px bg-white/20"></div>
        <span className="px-4 text-white/70 text-sm">or</span>
        <div className="flex-1 h-px bg-white/20"></div>
      </div>

      <Button
        data-testid="button-google-signin"
        onClick={handleGoogleSignIn}
        className="w-full bg-white/10 border border-white/20 text-white py-4 rounded-xl font-semibold flex items-center justify-center gap-3 transition-all hover:bg-white/20"
      >
        <svg className="w-5 h-5" viewBox="0 0 24 24">
          <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
          <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
          <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
          <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
        </svg>
        Continue with Google
      </Button>

      <Button
        data-testid="button-apple-signin"
        className="w-full bg-white/10 border border-white/20 text-white py-4 rounded-xl font-semibold flex items-center justify-center gap-3 transition-all hover:bg-white/20"
      >
        <Apple className="w-5 h-5" />
        Continue with Apple
      </Button>

      <p className="text-center text-white/70 text-sm mt-6">
        {isSignUp ? "Already have an account?" : "Don't have an account?"}
        <button
          data-testid="button-toggle-signup"
          className="text-white font-semibold underline ml-1"
          onClick={() => setIsSignUp(!isSignUp)}
        >
          {isSignUp ? "Sign in" : "Sign up"}
        </button>
      </p>
    </div>
  );
}

client/src/components/dashboard/service-.content:
import { Card } from "@/components/ui/card";
import { Service } from "@shared/schema";

interface ServiceCardProps {
  service: Service;
  selected?: boolean;
  onClick?: () => void;
}

export function ServiceCard({ service, selected = false, onClick }: ServiceCardProps) {
  const getServiceIcon = (name: string) => {
    if (name.toLowerCase().includes('wash') && name.toLowerCase().includes('iron')) {
      return "fas fa-magic";
    } else if (name.toLowerCase().includes('dry cleaning')) {
      return "fas fa-crown";
    }
    return "fas fa-tshirt";
  };

  const getServiceColor = (name: string) => {
    if (name.toLowerCase().includes('wash') && name.toLowerCase().includes('iron')) {
      return "from-emerald-100 to-emerald-200 text-emerald-600";
    } else if (name.toLowerCase().includes('dry cleaning')) {
      return "from-amber-100 to-amber-200 text-amber-600";
    }
    return "from-blue-100 to-blue-200 text-blue-600";
  };

  const formatPrice = (service: Service) => {
    if (service.pricingUnit === 'lb') {
      return `$${service.pricePerLb}/lb`;
    }
    return `$${service.pricePerItem}/item`;
  };

  return (
    <Card 
      data-testid={`service-card-${service.id}`}
      className={`service-card cursor-pointer transition-all ${selected ? 'selected' : ''}`}
      onClick={onClick}
    >
      <div className="flex items-center p-4">
        <div className={`w-16 h-16 bg-gradient-to-br ${getServiceColor(service.name)} rounded-xl flex items-center justify-center mr-4`}>
          <i className={`${getServiceIcon(service.name)} text-xl`}></i>
        </div>
        <div className="flex-1">
          <h4 className="font-semibold text-foreground" data-testid={`service-name-${service.id}`}>
            {service.name}
          </h4>
          <p className="text-muted-foreground text-sm" data-testid={`service-description-${service.id}`}>
            {service.description}
          </p>
          <p className="text-primary font-semibold text-lg" data-testid={`service-price-${service.id}`}>
            {formatPrice(service)}
          </p>
        </div>
        <i className="fas fa-chevron-right text-muted-foreground"></i>
      </div>
    </Card>
  );
}

client/src/components/layout/floating-na.content:
import { useLocation } from "wouter";
import { Home, Plus, History, User, Settings } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";

export function FloatingNavigation() {
  const [location, navigate] = useLocation();
  const { userProfile } = useAuth();

  const tabs = [
    { id: "dashboard", icon: Home, path: "/dashboard" },
    { id: "order", icon: Plus, path: "/order" },
    { id: "history", icon: History, path: "/history" },
    { id: "profile", icon: User, path: "/profile" },
    ...(userProfile?.role === 'admin' ? [{ id: "admin", icon: Settings, path: "/admin" }] : []),
  ];

  return (
    <div className="floating-tabs">
      {tabs.map((tab) => {
        const Icon = tab.icon;
        const isActive = location === tab.path;

        return (
          <button
            key={tab.id}
            data-testid={`tab-${tab.id}`}
            className={`tab-button ${isActive ? 'active' : ''}`}
            onClick={() => navigate(tab.path)}
          >
            <Icon size={20} />
          </button>
        );
      })}
    </div>
  );
}

client/src/components/order/order-form.t.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card } from "@/components/ui/card";
import { Service } from "@shared/schema";
import { useOrders } from "@/hooks/use-orders";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { Camera, Minus, Plus } from "lucide-react";

interface OrderFormProps {
  selectedService: Service;
}

export function OrderForm({ selectedService }: OrderFormProps) {
  const [quantity, setQuantity] = useState(1);
  const [pickupDate, setPickupDate] = useState("");
  const [pickupTimeSlot, setPickupTimeSlot] = useState("");
  const [notes, setNotes] = useState("");
  const [photos, setPhotos] = useState<string[]>([]);

  const { createOrder, isCreating } = useOrders();
  const { toast } = useToast();
  const [, navigate] = useLocation();

  const timeSlots = ["9:00 AM", "1:00 PM", "5:00 PM", "7:00 PM"];

  const calculateTotal = () => {
    const serviceFee = 2.00;
    let subtotal = 0;

    if (selectedService.pricingUnit === 'lb') {
      subtotal = quantity * Number(selectedService.pricePerLb);
    } else {
      subtotal = quantity * Number(selectedService.pricePerItem);
    }

    return {
      subtotal,
      serviceFee,
      total: subtotal + serviceFee,
    };
  };

  const handleSubmit = async () => {
    if (!pickupDate || !pickupTimeSlot) {
      toast({
        title: "Missing Information",
        description: "Please select pickup date and time",
        variant: "destructive",
      });
      return;
    }

    const { subtotal, serviceFee, total } = calculateTotal();

    const orderData = {
      serviceId: selectedService.id,
      quantity: quantity.toString(),
      subtotal: subtotal.toString(),
      serviceFee: serviceFee.toString(),
      total: total.toString(),
      pickupDate: new Date(pickupDate + 'T00:00:00'),
      pickupTimeSlot,
      notes: notes || undefined,
      photos: photos.length > 0 ? photos : undefined,
    };

    try {
      const order = await new Promise((resolve, reject) => {
        createOrder(orderData, {
          onSuccess: resolve,
          onError: reject,
        });
      });

      // Navigate to checkout with order ID
      navigate(`/checkout?orderId=${(order as any).id}`);
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to create order",
        variant: "destructive",
      });
    }
  };

  const { subtotal, serviceFee, total } = calculateTotal();

  return (
    <div className="space-y-6">
      {/* Selected Service */}
      <Card className="p-6">
        <h3 className="text-lg font-semibold text-foreground mb-4">Selected Service</h3>
        <div className="service-card selected">
          <div className="flex items-center">
            <div className="w-12 h-12 bg-gradient-to-br from-emerald-100 to-emerald-200 rounded-xl flex items-center justify-center mr-3">
              <i className="fas fa-magic text-emerald-600"></i>
            </div>
            <div className="flex-1">
              <h4 className="font-semibold text-foreground" data-testid="selected-service-name">
                {selectedService.name}
              </h4>
              <p className="text-primary font-semibold" data-testid="selected-service-price">
                ${selectedService.pricingUnit === 'lb' ? selectedService.pricePerLb : selectedService.pricePerItem}/{selectedService.pricingUnit}
              </p>
            </div>
          </div>
        </div>
      </Card>

      {/* Quantity Selection */}
      <Card className="p-6">
        <h3 className="text-lg font-semibold text-foreground mb-4">
          {selectedService.pricingUnit === 'lb' ? 'Weight' : 'Quantity'}
        </h3>
        <div className="flex items-center justify-center space-x-6">
          <Button
            data-testid="button-decrease-quantity"
            variant="outline"
            size="icon"
            className="w-12 h-12 rounded-full"
            onClick={() => setQuantity(Math.max(1, quantity - 0.5))}
          >
            <Minus className="h-4 w-4" />
          </Button>
          <div className="text-center">
            <p className="text-3xl font-bold text-foreground" data-testid="text-quantity">
              {quantity}
            </p>
            <p className="text-muted-foreground">{selectedService.pricingUnit}s</p>
          </div>
          <Button
            data-testid="button-increase-quantity"
            size="icon"
            className="w-12 h-12 rounded-full"
            onClick={() => setQuantity(quantity + 0.5)}
          >
            <Plus className="h-4 w-4" />
          </Button>
        </div>
      </Card>

      {/* Photo Upload */}
      <Card className="p-6">
        <h3 className="text-lg font-semibold text-foreground mb-4">Upload Photos (Optional)</h3>
        <div className="border-2 border-dashed border-border rounded-xl p-8 text-center">
          <Camera className="mx-auto text-3xl text-muted-foreground mb-4" size={48} />
          <p className="text-muted-foreground mb-4">Take photos of your items</p>
          <Button
            data-testid="button-add-photos"
            variant="outline"
          >
            Add Photos
          </Button>
        </div>
      </Card>

      {/* Schedule Pickup */}
      <Card className="p-6">
        <h3 className="text-lg font-semibold text-foreground mb-4">Schedule Pickup</h3>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-foreground mb-2">Date</label>
            <Input
              data-testid="input-pickup-date"
              type="date"
              value={pickupDate}
              onChange={(e) => setPickupDate(e.target.value)}
              min={new Date().toISOString().split('T')[0]}
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-foreground mb-2">Time Slot</label>
            <div className="grid grid-cols-2 gap-3">
              {timeSlots.map((slot) => (
                <Button
                  key={slot}
                  data-testid={`button-timeslot-${slot.replace(/[: ]/g, '-').toLowerCase()}`}
                  variant={pickupTimeSlot === slot ? "default" : "outline"}
                  onClick={() => setPickupTimeSlot(slot)}
                  className="py-3"
                >
                  {slot}
                </Button>
              ))}
            </div>
          </div>
        </div>
      </Card>

      {/* Order Summary */}
      <Card className="bg-gradient-to-r from-primary to-secondary text-white p-6">
        <h3 className="text-lg font-semibold mb-4">Order Summary</h3>
        <div className="space-y-2">
          <div className="flex justify-between" data-testid="order-summary-service">
            <span>{selectedService.name} ({quantity} {selectedService.pricingUnit}s)</span>
            <span data-testid="text-subtotal">${subtotal.toFixed(2)}</span>
          </div>
          <div className="flex justify-between" data-testid="order-summary-fee">
            <span>Service Fee</span>
            <span data-testid="text-service-fee">${serviceFee.toFixed(2)}</span>
          </div>
          <div className="border-t border-white/20 pt-2 mt-2">
            <div className="flex justify-between text-lg font-bold" data-testid="order-summary-total">
              <span>Total</span>
              <span data-testid="text-total">${total.toFixed(2)}</span>
            </div>
          </div>
        </div>
      </Card>

      {/* Submit Button */}
      <Button
        data-testid="button-proceed-payment"
        onClick={handleSubmit}
        disabled={isCreating}
        className="w-full py-4 text-lg font-semibold"
      >
        {isCreating ? "Creating Order..." : "Proceed to Payment"}
      </Button>
    </div>
  );
}

